mapper.select:
SELECT notice_key, row_num AS 번호, notice_title AS 제목, date_format(notice_date, '%Y.%m.%d') AS 등록일자, notice_hits AS 조회수 FROM (SELECT ROW_NUMBER() OVER (ORDER BY notice_date) AS row_num, notice_key , notice_title, notice_date, '%Y.%m.%d', notice_hits FROM notice
WHERE notice_date BETWEEN #{startDate} AND #{endDate}
) AS se
ORDER BY row_num DESC

mapper.update:
UPDATE notice
SET notice_title  = #{noticeTitle},
    notice_content= #{noticeContent}
WHERE notice_key = #{noticeKey}
